attached_assets/style_1756534726829.css.content:
:root {
    --lightblue: #ADD8E6; /* Light Blue */
    --white: #FFFFFF;
    --darkgrey: #36454F; /* Dark Grey */
    --text-color: #333;
    --light-text: #f4f4f4;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Montserrat', sans-serif;
    line-height: 1.6;
    background-color: var(--white);
    color: var(--text-color);
}

.container {
    width: 90%;
    max-width: 1200px;
    margin: auto;
    overflow: hidden;
    padding: 20px 0;
}

/* Header */
.main-header {
    background-color: var(--darkgrey);
    color: var(--white);
    padding: 20px 0;
    box-shadow: 0 2px 5px rgba(0,0,0,0.2);
}

.main-header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 1.8em;
    font-weight: 700;
    color: var(--white);
}

.main-nav ul {
    list-style: none;
}

.main-nav ul li {
    display: inline-block;
    margin-left: 20px;
}

.main-nav ul li a {
    color: var(--white);
    text-decoration: none;
    font-weight: 600;
    padding: 5px 10px;
    transition: color 0.3s ease, background-color 0.3s ease;
    border-radius: 5px;
}

.main-nav ul li a:hover,
.main-nav ul li a.active {
    color: var(--darkgrey);
    background-color: var(--lightblue);
}

.hero-image {
    background: url("images/poster.png") no-repeat center center/cover;
    height: 1080px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--white);
    text-align: center;
    position: relative;
    overflow: hidden; /* To handle potential image pop-up overflow */
}



/* Mission Statement */
.mission-statement {
    background-color: var(--lightblue);
    color: var(--darkgrey);
    padding: 60px 0;
    text-align: center;
}

.mission-statement h2 {
    font-size: 2.5em;
    margin-bottom: 20px;
    color: var(--darkgrey);
}

.mission-statement p {
    font-size: 1.2em;
    max-width: 800px;
    margin: 0 auto;
    line-height: 1.8;
}

/* News Preview */
.news-preview {
    padding: 80px 0;
    background-color: var(--white);
    text-align: center;
}

.news-preview h2 {
    font-size: 2.5em;
    margin-bottom: 50px;
    color: var(--darkgrey);
}

.news-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
}

.news-item {
    background-color: var(--white);
    border: 1px solid #ddd;
    border-radius: 8px;
    overflow: hidden;
    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    position: relative; /* For image hover effect */
}

.news-item:hover {
    transform: translateY(-10px);
    box-shadow: 0 8px 20px rgba(0,0,0,0.2);
}

.news-item img {
    width: 100%;
    height: 200px;
    object-fit: cover;
    display: block;
    transition: transform 0.4s ease-in-out; /* Pop-up animation */
}

.news-item:hover img {
    transform: scale(1.05);
}

.news-item h3 {
    font-size: 1.5em;
    margin: 20px 15px 10px;
    color: var(--darkgrey);
}

.news-item p {
    font-size: 1em;
    margin: 0 15px 20px;
    color: var(--text-color);
}

.news-item .read-more {
    display: inline-block;
    background-color: var(--lightblue);
    color: var(--darkgrey);
    padding: 10px 20px;
    border-radius: 5px;
    text-decoration: none;
    font-weight: 600;
    margin-bottom: 20px;
    transition: background-color 0.3s ease, color 0.3s ease;
}

.news-item .read-more:hover {
    background-color: var(--darkgrey);
    color: var(--white);
}

/* Footer */
.main-footer {
    background-color: var(--darkgrey);
    color: var(--light-text);
    text-align: center;
    padding: 40px 0;
    margin-top: 50px; /* To separate from the news section */
}

.main-footer p {
    font-size: 0.9em;
}

/* Responsive Design */
@media (max-width: 768px) {
    .main-header .container {
        flex-direction: column;
        text-align: center;
    }

    .main-nav ul {
        margin-top: 15px;
    }

    .main-nav ul li {
        margin: 0 10px;
    }

    .news-grid {
        grid-template-columns: 1fr;
    }
}

