        updatedAt: new Date()
      },
      {
        id: "officer",
        name: "Officer",
        description: "Executive leadership positions",
        displayOrder: 2,
        isActive: true,
        createdAt: new Date(),
        updatedAt: new Date()
      },
      {
        id: "active-member",
        name: "Active Member",
        description: "Regular participating members",
        displayOrder: 3,
        isActive: true,
        createdAt: new Date(),
        updatedAt: new Date()
      },
      {
        id: "faculty-advisors",
        name: "Faculty Advisors",
