  --popover-foreground: var(--ink);
  --primary: var(--primary-blue);
  --primary-foreground: var(--paper);
  --secondary: var(--mist);
  --secondary-foreground: var(--ink);
  --muted: var(--mist);
  --muted-foreground: var(--slate);
  --accent: var(--secondary-teal);
  --accent-foreground: var(--paper);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: var(--paper);
  --border: hsl(210, 8%, 88%);
  --border-subtle: hsl(210, 4%, 94%);
  --input: hsl(210, 8%, 90%);
  --ring: var(--primary-blue);
  --chart-1: var(--primary-blue);
  --chart-2: var(--secondary-teal);
  --chart-3: hsl(42, 93%, 56%);
  --chart-4: hsl(147, 79%, 42%);
  --chart-5: hsl(341, 75%, 51%);
  --sidebar: var(--paper);
  --sidebar-foreground: var(--ink);
  --sidebar-primary: var(--primary-blue);
  --sidebar-primary-foreground: var(--paper);
  --sidebar-accent: var(--mist);
  --sidebar-accent-foreground: var(--ink);
  --sidebar-border: hsl(210, 4%, 92%);
