  --background-muted: hsl(210, 10%, 8%);
  --foreground: var(--paper);
  --card: var(--graphite);
  --card-subtle: hsl(210, 8%, 18%);
  --card-foreground: var(--paper);
  --popover: var(--graphite);
  --popover-foreground: var(--paper);
  --primary: var(--primary-blue);
  --primary-foreground: var(--paper);
  --secondary: var(--slate);
  --secondary-foreground: var(--paper);
  --muted: var(--slate);
  --muted-foreground: var(--mist);
  --accent: var(--secondary-teal);
  --accent-foreground: var(--paper);
  --destructive: hsl(0, 63%, 31%);
  --destructive-foreground: var(--paper);
  --border: hsl(210, 6%, 30%);
  --border-subtle: hsl(210, 6%, 20%);
  --input: var(--slate);
  --ring: var(--primary-blue);
  --sidebar: var(--graphite);
  --sidebar-foreground: var(--paper);
  --sidebar-primary: var(--primary-blue);
  --sidebar-primary-foreground: var(--paper);
  --sidebar-accent: var(--slate);
  --sidebar-accent-foreground: var(--paper);
  --sidebar-border: var(--slate);
  --sidebar-ring: var(--primary-blue);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Premium Typography System */
  .font-display {
    font-weight: 700 !important;
    letter-spacing: 0.01em;
    line-height: 1.2;
    -webkit-text-stroke: 0.15px currentColor;
  }

  /* Large headings - refined and elegant */
  h1.font-display, h2.font-display {
    letter-spacing: 0.02em;
    font-weight: 700 !important;
    -webkit-text-stroke: 0.2px currentColor;
    line-height: 1.1;
  }

  /* Logo - sophisticated branding */
  .font-display.text-xl {
    letter-spacing: 0.05em;
    -webkit-text-stroke: 0.15px currentColor;
  }

  /* Premium Motion System */
  .luxury-hover {
    transition: all var(--duration-fast) var(--ease-luxury);
  }

  .luxury-hover:hover {
    transform: translateY(-1px);
    box-shadow: var(--shadow-medium);
  }

  .luxury-press:active {
    transform: scale(0.98);
    transition: transform 100ms ease-out;
  }

  /* Custom Animations */
  @keyframes luxury-fade-in {
    from {
      opacity: 0;
      transform: translateY(8px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes luxury-scale-in {
    from {
      opacity: 0;
      transform: scale(0.95);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  .luxury-fade-in {
    animation: luxury-fade-in var(--duration-medium) var(--ease-luxury) both;
  }

  .luxury-scale-in {
    animation: luxury-scale-in var(--duration-medium) var(--ease-luxury) both;
  }


  /* Premium Texture Effects */
  .texture-subtle {
    background-image: 
      radial-gradient(circle at 1px 1px, rgba(255,255,255,0.15) 1px, transparent 0);
    background-size: 20px 20px;
  }

  .texture-noise {
    background-image: 
      url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.02'/%3E%3C/svg%3E");
  }

  /* Enhanced Surface Hierarchy */
  .surface-elevated {
    background: hsl(from var(--background) h s calc(l + 1%));
    border: 1px solid var(--border-subtle);
  }

  .surface-sunken {
    background: hsl(from var(--background) h s calc(l - 1%));
    border: 1px solid var(--border);
  }

  /* Respect reduced motion preferences */
  @media (prefers-reduced-motion: reduce) {
    .luxury-hover,
    .luxury-press,
    .luxury-fade-in,
    .luxury-scale-in {
      animation: none;
      transition: none;
    }
  }
}

/* Print-friendly styles for medical information */
@media print {
  /* Hide navigation and non-essential elements when printing */
  header, footer, nav, .no-print {
    display: none !important;
  }

  /* Optimize page layout for print */
  * {
    background: white !important;
    color: black !important;
    box-shadow: none !important;
    text-shadow: none !important;
  }

  /* Ensure content fits well on printed pages */
  body {
    font-size: 12pt !important;
    line-height: 1.4 !important;
    margin: 0.5in !important;
  }

  /* Make headings stand out in print */
  h1, h2, h3, h4, h5, h6 {
    page-break-after: avoid;
    font-weight: bold !important;
    margin-top: 1em !important;
    margin-bottom: 0.5em !important;
  }

  /* Ensure proper spacing for medical content */
  p, li {
    orphans: 3;
    widows: 3;
  }

  /* Handle images appropriately */
  img {
    max-width: 100% !important;
    height: auto !important;
    page-break-inside: avoid;
  }

  /* Style links to show URLs */
  a::after {
    content: " (" attr(href) ")";
    font-size: 10pt;
    color: #666;
  }

  /* Remove hover effects and animations for print */
  * {
    transition: none !important;
    animation: none !important;
  }

  /* Ensure good contrast for text */
  .text-primary, .text-blue-600, .text-teal-600, .text-green-600 {
    color: #000 !important;
  }

  /* Page breaks for better content flow */
  .page-break {
    page-break-before: always;
  }

  /* Style cards and containers for print */
  .card, .bg-white, .bg-gray-50, .bg-blue-50, .bg-green-50, .bg-teal-50 {
    background: white !important;
    border: 1px solid #ccc !important;
    border-radius: 0 !important;
    margin-bottom: 1em !important;
    padding: 1em !important;
  }
}

/* Custom gradient utilities */
.bg-gradient-radial {
  background: radial-gradient(circle, var(--tw-gradient-stops));
}

