import { useState, useEffect } from "react";
import { Menu, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Link, useLocation } from "wouter";
import { Loading } from "@/components/ui/loading";
import logoImage from "@assets/logo_1756537062633.jpg";

export default function Header() {
  const [location] = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isScrolledPastHero, setIsScrolledPastHero] = useState(false);
  const [loadingNav, setLoadingNav] = useState(false);

  const isActive = (path: string) => {
    if (path === "/" && location === "/") return true;
    if (path !== "/" && location.startsWith(path)) return true;
    return false;
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setMobileMenuOpen(false);
  };

  const handleMobileNavClick = () => {
    closeMobileMenu();
    // Scroll to top when navigating on mobile
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleNavClick = () => {
    setLoadingNav(true);
    setTimeout(() => {
      // Scroll to top when navigating on desktop
      window.scrollTo({ top: 0, behavior: 'smooth' });
      setLoadingNav(false);
    }, 300);
  };

  // Scroll detection for home page header transparency
  useEffect(() => {
    if (location !== "/") {
      setIsScrolledPastHero(true); // Always solid on non-home pages
      return;
    }

    const handleScroll = () => {
      const scrollY = window.scrollY;
      const heroHeight = window.innerHeight; // Hero is h-screen (full viewport height)

      // Header becomes transparent when scrolled into hero (past 50px) and solid again when past hero
      const inHeroArea = scrollY > 50 && scrollY < heroHeight * 0.9;
      setIsScrolledPastHero(!inHeroArea); // Inverted logic: false = transparent, true = solid
    };

    // Initial check
    handleScroll();

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [location]);

  const isHomePage = location === "/";
  const isTransparent = isHomePage && !isScrolledPastHero;

  return (
    <header 
      className={`sticky top-0 z-50 transition-all duration-300 ease-in-out border-b ${
        isTransparent 
          ? 'bg-black/20 dark:bg-black/20 border-white/20 backdrop-blur-md' 
          : 'bg-background dark:bg-background border-border surface-elevated'
      }`} 
      style={isTransparent ? {} : { boxShadow: 'var(--shadow-subtle)' }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-20">
          {/* Logo */}
          <Link href="/">
            <div className="flex items-center space-x-4 luxury-hover" data-testid="logo">
              <img 
                src={logoImage}
                alt="ISB Medical Society Logo" 
                className="w-12 h-12 rounded-full object-cover"
                data-testid="logo-image"
              />
              <span className={`text-2xl font-display transition-colors duration-300 ${
                isTransparent ? 'text-white' : 'text-primary'
              }`}>ISB Medical Society</span>
            </div>
          </Link>

          {/* Center Navigation */}
          <nav className="hidden lg:flex items-center space-x-8" data-testid="navigation">
            <Link 
              href="/" 
              className={`relative font-display font-medium text-2xl transition-colors duration-300 ${
                isActive("/") 
                  ? (isTransparent ? "text-white" : "text-primary")
                  : (isTransparent ? "text-white/80 hover:text-white" : "text-muted-foreground hover:text-foreground")
              }`} 
              data-testid="nav-home"
              onClick={handleNavClick}
            >
              Home
              {isActive("/") && <div className={`absolute -bottom-1 left-0 right-0 h-0.5 rounded-full transition-colors duration-300 ${
                isTransparent ? 'bg-white' : 'bg-primary'
              }`} />}
            </Link>
            <Link 
              href="/about" 
              className={`relative font-display font-medium text-2xl transition-colors duration-300 ${
                isActive("/about") 
                  ? (isTransparent ? "text-white" : "text-primary")
                  : (isTransparent ? "text-white/80 hover:text-white" : "text-muted-foreground hover:text-foreground")
              }`} 
              data-testid="nav-about"
              onClick={handleNavClick}
            >
              About
              {isActive("/about") && <div className={`absolute -bottom-1 left-0 right-0 h-0.5 rounded-full transition-colors duration-300 ${
                isTransparent ? 'bg-white' : 'bg-primary'
              }`} />}
            </Link>
            <Link 
              href="/news" 
              className={`relative font-display font-medium text-2xl transition-colors duration-300 ${
                isActive("/news") 
                  ? (isTransparent ? "text-white" : "text-primary")
                  : (isTransparent ? "text-white/80 hover:text-white" : "text-muted-foreground hover:text-foreground")
              }`} 
              data-testid="nav-news"
              onClick={handleNavClick}
            >
              News
              {isActive("/news") && <div className={`absolute -bottom-1 left-0 right-0 h-0.5 rounded-full transition-colors duration-300 ${
                isTransparent ? 'bg-white' : 'bg-primary'
              }`} />}
            </Link>
            <Link 
              href="/contact" 
              className={`relative font-display font-medium text-2xl transition-colors duration-300 ${
                isActive("/contact") 
                  ? (isTransparent ? "text-white" : "text-primary")
                  : (isTransparent ? "text-white/80 hover:text-white" : "text-muted-foreground hover:text-foreground")
              }`} 
              data-testid="nav-contact"
              onClick={handleNavClick}
            >
              Contact
              {isActive("/contact") && <div className={`absolute -bottom-1 left-0 right-0 h-0.5 rounded-full transition-colors duration-300 ${
                isTransparent ? 'bg-white' : 'bg-primary'
              }`} />}
            </Link>
          </nav>

          {/* Right side - CTA and Mobile Menu */}
          <div className="flex items-center space-x-4">
            {/* Premium CTA */}
            <Link href="/contact" className="hidden md:block" onClick={handleNavClick}>
              <Button 
                className={`px-6 py-2 luxury-hover luxury-press font-medium text-[20px] transition-all duration-300 ${
                  isTransparent 
                    ? 'bg-white/20 hover:bg-white/30 text-white border border-white/30 backdrop-blur-sm' 
                    : 'bg-primary hover:bg-primary/90 text-primary-foreground'
                }`}
                style={isTransparent ? {} : { boxShadow: 'var(--shadow-hairline)' }}
                data-testid="header-cta"
                disabled={loadingNav}
              >
                {loadingNav ? (
                  <Loading size="sm" variant="spinner" />
                ) : (
                  'Join Us'
                )}
              </Button>
            </Link>

            {/* Mobile menu button */}
            <Button 
              variant="ghost" 
              size="sm" 
              className={`lg:hidden p-2 luxury-hover transition-colors duration-300 ${
                isTransparent ? 'text-white/80 hover:text-white' : 'text-muted-foreground hover:text-foreground'
              }`}
              onClick={toggleMobileMenu}
              data-testid="mobile-menu-button"
            >
              {mobileMenuOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
            </Button>
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        <div className={`lg:hidden transition-all duration-300 ease-in-out ${mobileMenuOpen ? 'max-h-80 opacity-100' : 'max-h-0 opacity-0 overflow-hidden'}`}>
          <nav className="px-4 py-6 border-t border-border bg-background dark:bg-background" data-testid="mobile-navigation">
            <div className="space-y-3">
              <Link 
                href="/" 
                className={`block font-display font-medium text-2xl py-3 px-4 rounded-xl luxury-hover ${isActive("/") ? "text-primary bg-primary/10" : "text-muted-foreground hover:text-foreground hover:bg-muted/50"}`}
                onClick={handleMobileNavClick}
                data-testid="mobile-nav-home"
              >
                Home
              </Link>
              <Link 
                href="/about" 
                className={`block font-display font-medium text-2xl py-3 px-4 rounded-xl luxury-hover ${isActive("/about") ? "text-primary bg-primary/10" : "text-muted-foreground hover:text-foreground hover:bg-muted/50"}`}
                onClick={handleMobileNavClick}
                data-testid="mobile-nav-about"
              >
                About
              </Link>
              <Link 
                href="/news" 
                className={`block font-display font-medium text-2xl py-3 px-4 rounded-xl luxury-hover ${isActive("/news") ? "text-primary bg-primary/10" : "text-muted-foreground hover:text-foreground hover:bg-muted/50"}`}
                onClick={handleMobileNavClick}
                data-testid="mobile-nav-news"
              >
                News
              </Link>
              <Link 
                href="/contact" 
                className={`block font-display font-medium text-2xl py-3 px-4 rounded-xl luxury-hover ${isActive("/contact") ? "text-primary bg-primary/10" : "text-muted-foreground hover:text-foreground hover:bg-muted/50"}`}
                onClick={handleMobileNavClick}
                data-testid="mobile-nav-contact"
              >
                Contact
              </Link>

              {/* Mobile CTA */}
              <div className="pt-4 border-t border-border">
                <Link href="/contact" onClick={handleMobileNavClick}>
                  <Button 
                    className="w-full bg-primary hover:bg-primary/90 text-primary-foreground luxury-hover luxury-press text-2xl"
                    data-testid="mobile-header-cta"
                  >
                    Join Us
                  </Button>
                </Link>
              </div>
            </div>
          </nav>
        </div>
      </div>
    </header>
  );
}

client/src/components/hero.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Link, useLocation } from "wouter";
import { Loading } from "@/components/ui/loading";
import groupPhotoImage from "@assets/97ccae24-4d7b-48c9-a16e-40476198cbd1_1758466251232.png";
import hospitalVisitImage from "@assets/e5a0817e-1bad-4a67-bc34-45225337e332_1758466243134.png";
import educationImage from "@assets/16fd4d4d-d0b8-481f-821d-9d4b8ccae2f6_1758466251232.png";

const heroImages = [
  {
    src: groupPhotoImage,
    alt: "ISB Medical Society - First Aid Training Certification Group"
  },
  {
    src: hospitalVisitImage,
    alt: "ISB Medical Society - Hospital Community Outreach"
  },
  {
    src: educationImage,
    alt: "ISB Medical Society - Health Education Programs"
  }
];

export default function Hero() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);
  const [scrollOpacity, setScrollOpacity] = useState(1);
  const [location, navigate] = useLocation();
  const [loadingButton, setLoadingButton] = useState<string | null>(null);
  const [isTransitioning, setIsTransitioning] = useState(false);

  // Auto-advance carousel with transition effects
  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrentSlide((prev) => (prev + 1) % heroImages.length);
        setTimeout(() => {
          setIsTransitioning(false);
        }, 800); // Allow transition to complete
      }, 100);
    }, 6000); // 6 seconds per slide

    return () => clearInterval(interval);
  }, [isPlaying]);

  // Scroll fade effect
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const fadeStart = 0; // Start fading immediately
      const fadeEnd = 150; // Completely faded after 150px of scroll

      if (scrollY <= fadeStart) {
        setScrollOpacity(1);
      } else if (scrollY >= fadeEnd) {
        setScrollOpacity(0);
      } else {
        // Calculate opacity between fadeStart and fadeEnd
        const fadeProgress = scrollY / fadeEnd;
        setScrollOpacity(1 - fadeProgress);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Pause on user interaction
  const handleInteraction = () => {
    setIsPlaying(false);
    setTimeout(() => setIsPlaying(true), 10000); // Resume after 10s
  };

  const goToSlide = (index: number) => {
    setIsTransitioning(true);
    setTimeout(() => {
      setCurrentSlide(index);
      setTimeout(() => {
        setIsTransitioning(false);
      }, 800);
    }, 100);
    handleInteraction();
  };

  // Scroll to next section
  const scrollToNext = () => {
    const heroSection = document.querySelector('[data-testid="hero-section"]');
    if (heroSection) {
      const nextSection = heroSection.nextElementSibling;
      if (nextSection) {
        nextSection.scrollIntoView({ 
          behavior: 'smooth',
          block: 'start'
        });
      }
    }
  };

  // Handle Join Our Mission button click
  const handleJoinClick = () => {
    setLoadingButton('join');
    setTimeout(() => {
      if (location === '/contact') {
        // If already on contact page, scroll to top
        window.scrollTo({ top: 0, behavior: 'smooth' });
      } else {
        // If not on contact page, navigate to contact page
        navigate('/contact');
      }
      setLoadingButton(null);
      handleInteraction();
    }, 500);
  };

  // Handle Learn More button click
  const handleLearnMoreClick = () => {
    setLoadingButton('learn');
    setTimeout(() => {
      if (location === '/about') {
        // If already on about page, scroll to top
        window.scrollTo({ top: 0, behavior: 'smooth' });
      } else {
        // If not on about page, navigate to about page
        navigate('/about');
      }
      setLoadingButton(null);
      handleInteraction();
    }, 500);
  };

  return (
    <section 
      className="relative h-screen overflow-hidden bg-black"
      data-testid="hero-section"
      onMouseEnter={() => setIsPlaying(false)}
      onMouseLeave={() => setIsPlaying(true)}
    >
      {/* Enhanced Background Carousel with Fade + Swipe + Heat Effects */}
      <div className="absolute inset-0 overflow-hidden">
        {heroImages.map((image, index) => {
          const isActive = index === currentSlide;
          const isPrev = index === (currentSlide - 1 + heroImages.length) % heroImages.length;
          const isNext = index === (currentSlide + 1) % heroImages.length;

          return (
            <div
              key={index}
              className={`absolute inset-0 transition-all duration-1000 ease-out ${
                isActive 
                  ? 'opacity-100 transform translate-x-0 scale-100' 
                  : isPrev 
                    ? 'opacity-0 transform -translate-x-full scale-105'
                    : isNext
                      ? 'opacity-0 transform translate-x-full scale-105'
                      : 'opacity-0 transform translate-x-0 scale-110'
              }`}
              style={{
                filter: isTransitioning && isActive 
                  ? 'blur(2px) saturate(1.3) contrast(1.1) brightness(1.1)' 
                  : 'blur(0px) saturate(1) contrast(1) brightness(1)',
                transition: 'all 800ms cubic-bezier(0.25, 0.46, 0.45, 0.94)'
              }}
            >
              <img 
                src={image.src}
                alt={image.alt}
                className="w-full h-full object-cover"
                style={{
                  transform: isTransitioning && isActive ? 'scale(1.02)' : 'scale(1)',
                  transition: 'transform 800ms ease-out'
                }}
                onLoad={() => console.log('Image loaded and visible:', image.src, 'slide:', index, 'current:', currentSlide)}
              />
              {/* Heat effect overlay during transitions */}
              {isTransitioning && isActive && (
                <div 
                  className="absolute inset-0 transition-opacity duration-300"
                  style={{
                    background: 'radial-gradient(circle at center, rgba(255, 69, 0, 0.15) 0%, rgba(255, 140, 0, 0.08) 40%, transparent 70%)',
                    mixBlendMode: 'soft-light'
                  }}
                />
              )}
              {/* Dim the background image only - fades away on scroll */}
              <div 
                className="absolute inset-0 bg-black/70 transition-opacity duration-300 ease-out"
                style={{ opacity: scrollOpacity }}
              />
            </div>
          );
        })}
      </div>

      {/* Split layout with dedicated text area */}
      <div className="absolute inset-0 grid lg:grid-cols-2 items-center">

        {/* Left side - Text content */}
        <div 
          className="h-full flex items-center justify-center p-8 sm:p-12 lg:p-16 transition-opacity duration-300 ease-out"
          style={{ 
            opacity: scrollOpacity
          }}
        >
          <div className="max-w-2xl space-y-12 luxury-fade-in">

            {/* Clean, readable typography */}
            <div className="space-y-8">
              <h1 
                className="text-5xl sm:text-6xl lg:text-7xl xl:text-8xl text-white leading-[1.1] tracking-wide"
                style={{ 
                  fontFamily: 'Beo, serif',
                  letterSpacing: '0.02em',
                  textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)',
                  fontWeight: '700'
                }}
                data-testid="hero-title"
              >
                <span className="block font-bold">Advancing</span>
                <span className="block text-primary font-bold mt-4">Healthcare</span>
                <span className="block text-white/80 text-2xl sm:text-3xl lg:text-4xl xl:text-5xl font-bold mt-6 tracking-[0.05em]">
                  Education & Equity
                </span>
              </h1>

              <p 
                className="text-base sm:text-lg lg:text-xl text-white/90 leading-loose font-light"
                style={{ 
                  letterSpacing: '0.025em',
                  textShadow: '1px 1px 2px rgba(0, 0, 0, 0.4)',
                  lineHeight: '1.8'
                }}
                data-testid="hero-description"
              >
                A student-led organization dedicated to educating our community about health, 
                advocating for healthcare equity, and supporting medical initiatives worldwide.
              </p>
            </div>

            {/* Premium CTAs - Enhanced to stand out */}
            <div 
              className="flex flex-col sm:flex-row gap-6 pt-8"
              style={{ 
                opacity: Math.max(0.85, scrollOpacity) // Always stay highly visible
              }}
            >
              <Button 
                size="lg" 
                className="border border-blue-400/60 hover:border-blue-300/80 bg-blue-500/25 hover:bg-blue-500/35 text-white hover:text-white backdrop-blur-md px-12 py-6 text-lg font-medium luxury-hover luxury-press rounded-xl"
                style={{ 
