import { useState } from "react";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";
import { ChevronDown, ChevronRight } from "lucide-react";
import { motion } from "framer-motion";
import { Loading } from "@/components/ui/loading";
import { useQuery } from "@tanstack/react-query";
import type { Member, MemberClass } from "@shared/schema";
import { OptimizedImage } from "@/components/OptimizedImage";
import blankPfpPath from "@assets/blank-pfp.png";

export default function About() {
  const [openSections, setOpenSections] = useState<{ [key: string]: boolean }>({
    websiteManager: true, // Default to website manager being open
    officers: true, // Default to officers being open
    members: false,
    advisors: false
  });
  const [loadingJoin, setLoadingJoin] = useState(false);

  const toggleSection = (section: string) => {
    setOpenSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  // Fetch members and member classes from API
  const { data: members = [], isLoading: membersLoading } = useQuery<Member[]>({
    queryKey: ["/api/members"],
  });

  const { data: memberClasses = [] } = useQuery<MemberClass[]>({
    queryKey: ["/api/member-classes"],
  });

  // Group members by class
  const getClassMembers = (className: string) => {
    const memberClass = memberClasses.find(mc => mc.name === className);
    if (!memberClass) return [];
    return members.filter(member => member.memberClassId === memberClass.id && member.isActive);
  };

  const websiteManagers = getClassMembers("Website Manager");
  const officers = getClassMembers("Officer");
  const activeMembers = getClassMembers("Active Member");
  const facultyAdvisors = getClassMembers("Faculty Advisors");


  const ProfileCard = ({ person, showPosition = false, showDepartment = false, showImage = true }: { person: Member, showPosition?: boolean, showDepartment?: boolean, showImage?: boolean }) => (
    <div className="flex items-center space-x-4 p-4 bg-card dark:bg-card border border-border rounded-xl luxury-hover luxury-press" style={{ boxShadow: 'var(--shadow-hairline)' }}>
      {showImage && (
        <OptimizedImage 
          src={person.image || person.thumbnail || blankPfpPath} 
          thumbnail={person.thumbnail}
          alt={person.name}
          className="w-14 h-14 rounded-full object-cover ring-2 ring-border"
        />
      )}
      <div className="flex-1 space-y-1">
        <h4 className="font-medium text-foreground">{person.name}</h4>
        {showPosition && person.role && (
          <p className="text-sm text-primary font-medium">{person.role}</p>
        )}
        {showDepartment && person.bio && (
          <p className="text-sm text-muted-foreground">{person.bio}</p>
        )}
      </div>
    </div>
  );

  const AccordionSection = ({ 
    title, 
    isOpen, 
    onToggle, 
    children, 
    testId 
  }: { 
    title: string, 
    isOpen: boolean, 
    onToggle: () => void, 
    children: React.ReactNode,
    testId: string 
  }) => (
    <div className="border-b border-border last:border-b-0 overflow-hidden bg-card dark:bg-card rounded-xl mb-3" style={{ boxShadow: 'var(--shadow-hairline)' }}>
      <button
        onClick={onToggle}
        className="w-full px-6 py-6 flex items-center justify-between text-left luxury-hover touch-manipulation"
        data-testid={testId}
      >
        <span className="font-display text-foreground tracking-wide text-lg">{title}</span>
        <ChevronRight className={`w-5 h-5 text-muted-foreground transition-transform duration-300 ${isOpen ? 'rotate-90' : 'rotate-0'}`} />
      </button>
      <div className={`transition-all duration-300 ease-in-out ${isOpen ? 'max-h-[500px] opacity-100' : 'max-h-0 opacity-0'}`}>
        <div className="px-6 pb-6 border-t border-border">
          <div className="pt-6">
            {children}
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-background dark:bg-background">
      <Header />
      <main className="py-24 luxury-fade-in">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

          {/* Centered Title */}
          <div className="text-center mb-20">
            <h1 className="text-3xl sm:text-4xl lg:text-5xl font-display text-foreground mb-6" data-testid="about-title">
              About <span className="text-primary">Us</span>
            </h1>
            <p className="text-lg text-muted-foreground max-w-2xl mx-auto">Get to know the people driving ISB Medical Society forward</p>
          </div>

          <div className="grid lg:grid-cols-2 gap-12 lg:gap-20 items-start">

            {/* Left Side - Organization Description */}
            <div className="luxury-scale-in" data-testid="about-content">
              <h2 className="text-2xl sm:text-3xl font-display text-foreground mb-8" data-testid="org-title">Our Organization</h2>

              <div className="space-y-8 text-muted-foreground leading-relaxed max-w-prose">
                <p data-testid="about-description" className="text-justify">
                  ISB MedSociety is more than just a student club â€” it's a community built by and for those who are passionate about the medical sciences. Founded on the belief that knowledge in medicine should not be confined to classrooms, our club provides a space where students can explore the world of healthcare, develop leadership skills, and make a meaningful impact.
                </p>

                <div>
                  <h3 className="font-display text-foreground mb-4 text-[30px]">Why We Exist</h3>
                  <p className="text-justify">
                    We believe the next generation of healthcare professionals must not only be knowledgeable but also empathetic, socially aware, and motivated to improve lives. ISB MedSociety nurtures this vision by creating opportunities to learn, to lead, and to give back.
                  </p>
                </div>
              </div>
            </div>

            {/* Right Side - Team Members Accordion */}
            <div className="luxury-fade-in" data-testid="members-section">
              <div className="space-y-4">

                <AccordionSection
                  title="Website Manager"
                  isOpen={openSections.websiteManager}
                  onToggle={() => toggleSection('websiteManager')}
                  testId="section-website-manager"
                >
                  <div className="space-y-2">
                    {websiteManagers.map((manager: Member, index: number) => (
                      <ProfileCard 
                        key={manager.id} 
                        person={manager} 
                        showPosition={true}
                      />
                    ))}
                  </div>
                </AccordionSection>

                <AccordionSection
                  title="Club Officers"
                  isOpen={openSections.officers}
                  onToggle={() => toggleSection('officers')}
                  testId="section-officers"
                >
                  <div className="space-y-2">
                    {officers.map((officer: Member, index: number) => (
                      <ProfileCard 
                        key={officer.id} 
                        person={officer} 
                        showPosition={true}
                      />
                    ))}
                  </div>
                </AccordionSection>

                <AccordionSection
                  title="Active Members"
                  isOpen={openSections.members}
                  onToggle={() => toggleSection('members')}
                  testId="section-members"
                >
                  <div className="space-y-2">
                    {activeMembers.map((member: Member, index: number) => (
                      <ProfileCard 
                        key={member.id} 
                        person={member}
                        showImage={true}
                      />
                    ))}
                  </div>
                </AccordionSection>

                <AccordionSection
                  title="Faculty Advisors"
                  isOpen={openSections.advisors}
                  onToggle={() => toggleSection('advisors')}
                  testId="section-advisors"
                >
                  <div className="space-y-2">
                    {facultyAdvisors.map((advisor: Member, index: number) => (
                      <ProfileCard 
                        key={advisor.id} 
                        person={advisor} 
                        showPosition={true}
                        showDepartment={true}
                      />
                    ))}
                  </div>
                </AccordionSection>

              </div>
            </div>

          </div>

          {/* Interested in Joining Section */}
          <motion.div 
            className="mt-16 lg:mt-20" 
            data-testid="joining-section"
            initial={{ opacity: 0, y: 60 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-100px" }}
            transition={{ duration: 0.8, ease: "easeOut" }}
          >
            <motion.div 
              className="bg-white rounded-lg shadow-lg p-8 lg:p-12"
              whileInView={{ 
                boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.15)" 
              }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              <motion.h2 
                className="text-2xl sm:text-3xl lg:text-4xl font-bold text-center text-gray-900 mb-8 font-display" 
                data-testid="joining-title"
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.6, 
                  delay: 0.3,
                  type: "spring",
                  stiffness: 120
                }}
              >
                Interested in Joining?
              </motion.h2>

              <div className="max-w-3xl mx-auto text-center">
                <motion.p 
                  className="text-lg text-gray-700 mb-8 leading-relaxed" 
                  data-testid="joining-description"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.5 }}
                >
                  ISB Medical Society welcomes students who are passionate about healthcare, medicine, and making a positive impact in our community.
                </motion.p>

                <motion.div 
                  className="mb-10"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.7 }}
                >
                  <motion.div
                    whileHover={{ 
                      scale: 1.05,
                      boxShadow: "0 20px 40px -10px rgba(37, 99, 235, 0.3)"
                    }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <Button 
                      size="lg" 
                      className="text-white bg-blue-600 hover:bg-blue-700 outline-none focus:outline-none focus-visible:outline-none !ring-0 focus:!ring-0 focus-visible:!ring-0 focus-visible:ring-transparent focus-visible:!ring-offset-0 transition-all duration-300 px-8 py-3 text-lg font-medium rounded-lg"
                      data-testid="button-join-now"
                      onClick={() => {
                        setLoadingJoin(true);
                        setTimeout(() => {
                          window.scrollTo({ top: 0, behavior: 'smooth' });
                          window.location.href = '/contact';
                          setLoadingJoin(false);
                        }, 500);
                      }}
                      disabled={loadingJoin}
                    >
                      {loadingJoin ? (
                        <Loading size="sm" variant="dots" />
                      ) : (
                        'Join Now'
                      )}
                    </Button>
                  </motion.div>
                </motion.div>

                <ul className="grid sm:grid-cols-2 lg:grid-cols-5 gap-6 text-gray-700 list-none">
