                          viewport={{ once: true }}
                          transition={{ 
                            duration: 0.4, 
                            delay: 1.2 + (index * 0.15),
                            type: "spring",
                            stiffness: 200
                          }}
                          whileHover={{
                            color: "#1d4ed8",
                            scale: 1.2,
                            transition: { duration: 0.2 }
                          }}
                        >
                          âœ“
                        </motion.span>
                      </motion.div>
                      <p className="font-medium text-center">{benefit.text}</p>
